import React, { useState, useEffect } from "react";
import "./index.css";
const SnackGame = () => {
  let blockMaping = require("../../json/maping.json");
  const [playerOneActiveIndex, setPlayerOneActiveIndex] = useState(1);
  const [playerTwoActiveIndex, setPlayerTwoActiveIndex] = useState(1);
  const [playerOneDice, setPlayerOneDice] = useState(0);
  const [playerTwoDice, setPlayerTwoDice] = useState(0);
  const [activePlayer, setActivePlayer] = useState("playerOne");

  const diceClick = player => {
    if (player == "playerOne" && activePlayer == "playerOne") {
      let randomNumber = Math.floor(Math.random() * 6) + 1;
      setPlayerOneDice(randomNumber);
      setActivePlayer("playerTwo");
      if (playerOneActiveIndex + randomNumber <= 100) {
        setPlayerOneActiveIndex(playerOneActiveIndex + randomNumber);
        let ladder = parseInt(
          document
            .getElementById(playerOneActiveIndex + randomNumber)
            .getAttribute("data-for-ladder")
        );
        let snake = parseInt(
          document
            .getElementById(playerOneActiveIndex + randomNumber)
            .getAttribute("data-for-snack")
        );
        if (ladder || snake) {
          setTimeout(function() {
            setPlayerOneActiveIndex(ladder || snake);
          }, 500);
        }

        if (playerOneActiveIndex + randomNumber == 100) {
          alert("player One win");
          setPlayerOneActiveIndex(1);
          setPlayerTwoActiveIndex(1);
          setPlayerOneDice(0);
          setPlayerTwoDice(0);
          setActivePlayer("playerOne");
        }
      }
    } else if (player == "playerTwo" && activePlayer == "playerTwo") {
      let randomNumber = Math.floor(Math.random() * 6) + 1;
      setPlayerTwoDice(randomNumber);
      setActivePlayer("playerOne");
      if (playerTwoActiveIndex + randomNumber <= 100) {
        setPlayerTwoActiveIndex(playerTwoActiveIndex + randomNumber);
        let ladder = parseInt(
          document
            .getElementById(playerTwoActiveIndex + randomNumber)
            .getAttribute("data-for-ladder")
        );
        let snake = parseInt(
          document
            .getElementById(playerTwoActiveIndex + randomNumber)
            .getAttribute("data-for-snack")
        );
        if (ladder || snake) {
          setTimeout(function() {
            setPlayerTwoActiveIndex(ladder || snake);
          }, 500);
        }

        if (playerTwoActiveIndex + randomNumber == 100) {
          alert("player Two win");
          setPlayerOneActiveIndex(1);
          setPlayerTwoActiveIndex(1);
          setPlayerOneDice(0);
          setPlayerTwoDice(0);
          setActivePlayer("playerOne");
        }
      }
    }
  };
  const restartGame = () => {
    setPlayerOneActiveIndex(1);
    setPlayerTwoActiveIndex(1);
    setPlayerOneDice(0);
    setPlayerTwoDice(0);
    setActivePlayer("playerOne");
  };
  return (
    <div className="snake-game-container">
      <button onClick={restartGame} className="restart-button">
        Restart
      </button>
      <div className="playerOne">
        playerOne
        <div className="dice-1">
          <button onClick={() => diceClick("playerOne")}>
            {playerOneDice}
          </button>
        </div>
      </div>
      <div className="playerTwo">
        playerTwo
        <div className="dice-2">
          <button onClick={() => diceClick("playerTwo")}>
            {playerTwoDice}
          </button>
        </div>
      </div>
      {blockMaping.map((row, index) => {
        return (
          <>
            <div className={`row${index + 1}`}>
              {row.map((value, rowIndex) => {
                return (
                  <div className={`block-${rowIndex + 1}`}>
                    <div
                      id={value.block}
                      data-for-ladder={value.ladder}
                      data-for-snack={value.snack}
                    >
                      {/* {value.ladder && <div className = {`ladder-from-to${value.block}`} > </div>}
                    {value.snack &&  <div className = {`snake-from-to${value.block}`}></div>} */}
                      {playerOneActiveIndex === value.block && (
                        <span className="playerOneCoin">@</span>
                      )}
                      {playerTwoActiveIndex === value.block && (
                        <span className="playerTwoCoin">@</span>
                      )}
                      {value.block}
                      {value.ladder && (
                        <span className="ladder-to">{`-> ${value.ladder}`}</span>
                      )}
                      {value.snack && (
                        <span className="snake-to">{`-> ${value.snack}`}</span>
                      )}
                      {/* <div class="oneRed"></div>
                      <div class="oneGreen"></div> */}
                    </div>
                  </div>
                );
              })}
            </div>
          </>
        );
      })}
    </div>
  );
};
export default SnackGame;
