import React, { useState } from "react";
import "./index.css";
const SnackGame = () => {
  let blockMaping = require("../../json/maping.json");
  const [playerOneActiveIndex, setPlayerOneActiveIndex] = useState(2);
  const [playerTwoActiveIndex, setPlayerTwoActiveIndex] = useState(10);
  return (
    <div className="snake-game-container">
      {blockMaping.map((row, index) => {
        return (
          <>
            <div className={`row${index + 1}`}>
              {row.map((value, rowIndex) => {
                return (
                  <div className={`block-${rowIndex + 1}`}>
                    <div
                      id={value.block}
                      data-for-ladder={value.ladder}
                      data-for-snack={value.snack}
                    >
                      {playerOneActiveIndex === value.block && <span>#</span>}
                      {playerTwoActiveIndex === value.block && <span>@</span>}
                      {value.block}
                      <div class="oneRed"></div>
                      <div class="oneGreen"></div>
                    </div>
                  </div>
                );
              })}
            </div>
          </>
        );
      })}
      <div>hello</div>
    </div>
  );
};
export default SnackGame;
